package com.wipro.java.collection;

public class Array {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Declaring an array
		int[] arr;;
		arr=new int[5];
		//assign values to the array
		arr[0]=1;
		arr[1]=2;
		arr[2]=3;
		arr[3]=4;
		arr[4]=5;
		//iterating through array to print the elements in the array
		for(int i=0;i<5;i++) {
			System.out.println(arr[i]);
		}

	}

}
